// Get the input data
const inputData = $input.first().json;
// Start with the select parameter
let queryString = "select=id, address_street, address_zip, beds, property_status, realtor_status, realtor_dwelling_type, realtor_list_price, offer_price, followup_notes, timestamp_scraped, zillow_url, color, tags, safmr, cover_photo_url, address_city, address_state, starred, telegram_internal_forum, telegram_realtor_forum, telegram_realtor_topic_id, contingent_listing_type";

// Function to handle array or string that looks like an array
function processArrayInput(input) {
  // Add explicit check for null/undefined
  if (input === null || input === undefined) {
    return [];
  }

  if (typeof input === 'string') {
    try {
      // Try to parse it as JSON
      return JSON.parse(input);
    } catch (e) {
      // If parsing fails, return empty array
      return [];
    }
  } else if (Array.isArray(input)) {
    return input;
  }
  return [];
}

// Process internal_statuses
if (inputData.internal_statuses) {
  const statuses = processArrayInput(inputData.internal_statuses);
  if (statuses && statuses.length > 0) {  // Add check for null before accessing length
    const statusList = "(" + statuses.map(status => `"${status}"`).join(",") + ")";
    queryString += "&property_status=in." + statusList;
  }
}

// Process market_statuses - MODIFIED to handle the special case for PENDING
if (inputData.market_statuses) {
  const marketStatuses = processArrayInput(inputData.market_statuses);
  if (marketStatuses && marketStatuses.length > 0) {
    // Check if "PENDING" is in the market_statuses
    const hasPending = marketStatuses.includes("PENDING");

    // Build the query to match any of the market statuses
    const marketStatusList = "(" + marketStatuses.map(status => `"${status}"`).join(",") + ")";

    if (hasPending) {
      // If PENDING is included, match either realtor_status in market_statuses OR contingent_listing_type is UNDER_CONTRACT
      queryString += "&or=(realtor_status.in." + marketStatusList + ",contingent_listing_type.eq.UNDER_CONTRACT)";
    } else {
      // Otherwise, just match realtor_status in market_statuses
      queryString += "&realtor_status=in." + marketStatusList;
    }
  }
}

// Process dwellings
if (inputData.dwellings) {
  const dwellings = processArrayInput(inputData.dwellings);
  if (dwellings && dwellings.length > 0) {  // Add check for null before accessing length
    const dwellingsList = "(" + dwellings.map(dwelling => `"${dwelling}"`).join(",") + ")";
    queryString += "&realtor_dwelling_type=in." + dwellingsList;
  }
}

// Process tags
if (inputData.tags) {
  const tags = processArrayInput(inputData.tags);
  if (tags && tags.length > 0) {  // Add check for null before accessing length
    const tagsList = "{" + tags.map(tag => `${tag}`).join(",") + "}";
    queryString += "&tags=ov." + tagsList;
  }
}

// Process starred parameter - only add it if it's explicitly provided
if (inputData.starred === true || inputData.starred === false) {
  // Only add if it's an explicit boolean
  queryString += `&starred=is.${inputData.starred}`;
} else if (typeof inputData.starred === 'string' && inputData.starred.trim() !== '') {
  // If it's a non-empty string, convert to boolean only if it's "true" or "false"
  const normalizedValue = inputData.starred.toLowerCase().trim();
  if (normalizedValue === 'true' || normalizedValue === 'false') {
    queryString += `&starred=is.${normalizedValue === 'true'}`;
  }
}

// Process search query and search columns
if (inputData.search_query && inputData.search_query.trim() !== '') {
  const searchColumns = processArrayInput(inputData.search_columns);
  if (searchColumns && searchColumns.length > 0) {  // Add check for null before accessing length
    // Create search conditions for each column
    const conditions = searchColumns.map(column => 
      `${column}.ilike.*${inputData.search_query}*`
    );

    // Join conditions with commas and wrap in parentheses
    const orCondition = "(" + conditions.join(",") + ")";

    // Add to query string
    queryString += "&or=" + orCondition;
  }
}

// Handle sorting with default and secondary sorting
let sortDirection = 'asc'; // Default sort direction
if (inputData.sort_direction && 
    (inputData.sort_direction.toLowerCase() === 'desc' || 
     inputData.sort_direction.toLowerCase() === 'asc')) {
  sortDirection = inputData.sort_direction.toLowerCase();
}
if (inputData.sort_column) {
  // Primary sort by provided column, secondary sort by id in same direction
  queryString += `&order=${inputData.sort_column}.${sortDirection},id.${sortDirection}`;
} else {
  // Default sort by property_status and then id
  queryString += "&order=property_status.asc,id.asc";
}

// Return data in the format N8N expects
return [{
  json: {
    queryString: queryString
  }
}];
